/*########################################################################################
Simpy the HELM FORM Factor for tests and simplicity
########################################################################################*/
double fsi(int A, double Er){
    double s = 0.9;                                         // nuclear skin thickness in fm
    double a = 0.52;                                        // surface thickness of the nucleus in fm
    double c=1.23*pow((double)A*1.0,(1.0/3.0))-0.6;             // parameter of the Fermi distribution
    double rn=sqrt(c*c-5.0*s*s+7.0/3.0*M_PI*M_PI*a*a);      // effective radius
    double q=(6.92e-3)*sqrt((double)A*1.0)*sqrt(Er);            // transfered momentum
    
    return 3.0*((sin(q*rn)-q*rn*cos(q*rn))/pow(q*rn,3.0))*exp(-pow(q*s,2.0)/2.0);
}



/*########################################################################################
Generic polynomial function used to parametrize each nuclear response
########################################################################################*/




double f(double Er, int A, int Z, double a0, double a1, double a2, double a3, double a4,
         double a5, double a6, double a7, double a8, double a9, double a10)
{


    /*MEGA+=1;*/

    double mn = Z * 0.938 + (A-Z) * 0.940; // GeV
    double q2 = 2.*mn*Er*1.e-6;//GeV
    /*double q = sqrt(2.*mn*Er*1.e-6);//GeV*/
    /*double acub =  pow(A,-1./3.); // FIXME The right thing to do is to have all these guys as a lookup table*/
    /*double b = sqrt(41.467/(45.*acub-25.*acub*acub))/0.197; //oscillator param.*/
    /*double b = B[A-1];*/
    double b2 = B2[A-1];

    /*printf("FARG %d%d %f\n", A, Z, Er);*/
    // The original code --- massive CPU time spent in pow
    /*double b = sqrt(41.467/(45.*pow(A,-1./3.)-25.*pow(A,-2./3.)))/0.197; //oscillator param.*/
    /*double y = pow(q*b/2.,2.);*/
    /*double old = exp(-2.*y) * ( a0 +a1  * y +a2  * pow(y,2.) +a3  * pow(y,3.)*/
        /*+a4  * pow(y,4.) +a5  * pow(y,5.) +a6  * pow(y,6.) +a7  * pow(y,7.) +a8*/
        /** pow(y,8.) +a9  * pow(y,9.) +a10 * pow(y,10.));*/

    // This can also be done in a loop if all the a were in an array
    const double w   = 0.25*q2*b2;
    const double w2  = w*w;
    const double w3  = w2*w;
    const double w4  = w2*w2;
    const double w5  = w2*w3;
    const double w6  = w3*w3;
    const double w7  = w3*w4;
    const double w8  = w4*w4;
    const double w9  = w4*w5;
    const double w10 = w5*w5;

    double new = exp(-2.*w) * ( a0 +  a1*w + a2*w2 + a3*w3 + a4*w4 + a5*w5 + a6*w6 + a7*w7 + a8*w8 + a9*w9 + a10*w10);

    return new;
}


double fholger(double Er, int A, int Z, double const * coeffs)
{
    double mn = Z * 0.938 + (A-Z) * 0.940; // GeV
    double q2 = 2.*mn*Er*1.e-6;//GeV
    double b2 = B2[A-1];

    const double w   = 0.25*q2*b2;
    /*const double w2  = w*w;*/
    /*const double w3  = w2*w;*/
    /*const double w4  = w2*w2;*/
    /*const double w5  = w2*w3;*/
    /*const double w6  = w3*w3;*/
    /*const double w7  = w3*w4;*/
    /*const double w8  = w4*w4;*/
    /*const double w9  = w4*w5;*/
    /*const double w10 = w5*w5;*/

    /*double new = exp(-2.*w) * ( coeffs[0] +  coeffs[1]*w + coeffs[2]*w2 + coeffs[3]*w3 + coeffs[4]*w4 + coeffs[5]*w5 + coeffs[6]*w6 + coeffs[7]*w7 + coeffs[8]*w8 + coeffs[9]*w9 + coeffs[10]*w10);*/

    /*return new;*/
    double t = 1.0;
    double result = coeffs[0];

    for (int i=1;i<10;i++)
    {
      t*=w;
      result += t*coeffs[i];
    }

    return  exp(-2.*w) * result;
}



